package classwork;

public class TernaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n =5;
		boolean retflag = (n%2==0) ? true  : false;
		if(retflag) {
			System.out.println("even number");
		}else {
			System.out.println("odd number");
		}
	}

}
