package classwork;

public class WhielLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i =100;
		while (i > 1) {
			System.out.println(i);
			i=i-2;
		}
		System.out.println("after loop");
		
		int a =1;
		while ( a < 100) {
			System.out.println(a);
			++a;
		}
		System.out.println("after loop");
				
				
	}

}
